"""    Creates python resource file for PyQt5 with pyrcc5"""import sysimport lxml.etree as Etfrom pathlib import Pathfrom shlex import split as shell_syntaxfrom subprocess import runfrom modules.settings import Settingsfrom modules.app_globals import Resourcescripts_dir = Path(sys.executable).parent  # eg. C:/Python/Scriptsargs = scripts_dir.as_posix() + r"/pyrcc5.exe -compress 9 -o gui_resource.py res/gui_resource.qrc"print(shell_syntax(args))run(shell_syntax(args), shell=True)# Update resource pathswith open('res/gui_resource.qrc', 'r') as f:    xml = Et.parse(f)for e in xml.iterfind('qresource'):    prefix = e.get('prefix')    if not prefix:        continue    print('---- Prefix: {} ----'.format(prefix))    for file in e.iter('file'):        file_path = file.text        if not file_path:            continue        rsc_path = ':/{}/{}'.format(prefix, file_path)        print(rsc_path)        Resource.icon_paths[Path(file_path).stem] = rsc_pathfor ui_file in Path('.').glob('*.ui'):    Resource.ui_paths[ui_file.stem] = ui_file.as_posix()# Write to jsonSettings.save(Resource, Path('gui_resource_paths.json').absolute())sys.exit()